import React, { useState } from 'react';
import PropTypes from 'prop-types';
import './Login.css';
//import { ReactComponent as eye } from './eye.svg';
//import { ReactComponent as eye_off } from './eye-off-outline.svg';

async function loginUser(credentials) {
 return fetch('http://localhost:8080/login', {
   method: 'POST',
   headers: {
     'Content-Type': 'application/json'
   },
   body: JSON.stringify(credentials)
 })
   .then(data => data.json())
}

export default function Login({ setToken }) {
  const [username, setUserName] = useState();
  const [password, setPassword] = useState();

  const handleSubmit = async e => {
    e.preventDefault();
    const token = await loginUser({
      username,
      password
    });
    setToken(token);
  }

  const handlePasswordVisibility = () => {
    setShowPassword(!showPassword);
 }

 return(
    <div className="login-wrapper">
      <form onSubmit={handleSubmit}>
        <label>
          <p className='text'>login</p>
          <input type="text" onChange={e => setUserName(e.target.value)} />
        </label>
        <label>
          <password>
            <p className='text'>password</p>
            <button type="button" className='eye' onClick={handlePasswordVisibility}>{showPassword ? 'Eye' : 'Eye-off'}</button>
            <input type={showPassword ? 'text' : 'password'} onChange={e => setPassword(e.target.value)} />
          </password>
        </label>
        <div>
          <button type="sign_in" className='sign_in'>Sign In</button>
        </div>
      </form>
    </div>
 )
}

Login.propTypes = {
 setToken: PropTypes.func.isRequired
};
